package com.maps.bagmap.test;


import com.maps.bagmap.service.firebase.FirebaseStorageService;
import com.maps.bagmap.service.firebase.UploadInput;

public class UploadTest {
	
	public static void main(String[] args) {
		
		FirebaseStorageService firebaseStorage = new FirebaseStorageService();
	
		UploadInput upload = new UploadInput();
		
		upload.setFilename("hotel.png");
		upload.setMimeType("image/png");
		upload.setBase64("data:image/png;base64,");
		
		String url = null;
		
		try {
			firebaseStorage.init();
			url = firebaseStorage.upload(upload, "icons/");
		} catch (Exception e) {
			e.printStackTrace();
		}
		
		System.out.println(url);

	}

}
